import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 
import './styles/ManageLeave.css';

const ManageLeave = () => {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const navigate = useNavigate(); 

  useEffect(() => {
    fetchLeaveRequests();
  }, []);

  // Fetch leave requests from the backend
  const fetchLeaveRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/leaves');
      setLeaveRequests(response.data);
    } catch (error) {
      console.error('Error fetching leave requests:', error);
    }
  };

  // Approve or reject leave requests
const handleLeaveApproval = async (id, status, employeeId, startDate, endDate) => {
  try {
    await axios.patch(`http://localhost:5000/api/leaves/${id}`, { status });
    if (status === 'Approved') {
      const start = new Date(startDate);
      const end = new Date(endDate);
      const leaveDays = Math.ceil((end - start) / (1000 * 60 * 60 * 24)) + 1;

      // Update the employee's leave balance by reducing it by the calculated leave days
      await axios.patch(`http://localhost:5000/api/employees/${employeeId}/leave-balance`, { leaveDays });
    }
    fetchLeaveRequests(); // Refresh the list after approval/rejection
  } catch (error) {
    console.error('Error updating leave status:', error);
  }
};

  return (
    <div className="manage-leave">
      <h2>LEAVE REQUESTS</h2>
      {leaveRequests.length === 0 ? (
        <p>No leave requests so far</p>
      ) : (
        <ul>
          {leaveRequests.map(req => {
            const startDate = new Date(req.startDate).toLocaleDateString('en-GB', {
              day: 'numeric',
              month: 'long',
              year: 'numeric',
            });
           
            const endDate = new Date(req.endDate).toLocaleDateString('en-GB', {
              day: 'numeric',
              month: 'long',
              year: 'numeric',
            });

            return (
              <li key={req._id} className={`leave-request ${req.status.toLowerCase()}`}>
                <div>
                  <strong>{req.name}</strong> requested <strong>{req.leaveType}</strong> from <strong>{startDate}</strong> to <strong>{endDate}</strong>.
                </div>
                <div className="status-container">
                  <span>Status: </span>
                  <span className={`status ${req.status.toLowerCase()}`}>{req.status}</span>
                </div>
                {req.status === 'Pending' && (
                  <div className="action-buttons">
                    <button className="approve" onClick={() => handleLeaveApproval(req._id, 'Approved', req.employeeId, req.startDate, req.endDate)}>Approve</button>
                    <button className="reject" onClick={() => handleLeaveApproval(req._id, 'Rejected')}>Reject</button>
                  </div>
                )}
              </li>
            );
          })}
        </ul>
      )}
      {/* Back to Dashboard Button */}
      <button className="back-to-dashboard" onClick={() => navigate('/admin-dashboard')}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default ManageLeave;
