import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 
import './styles/EmployeeDashboard.css';
import { logout } from './logout';

function EmployeeDashboard() {
  const [employee, setEmployee] = useState(null);
  const navigate = useNavigate(); 

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Retrieve the token from localStorage
        const token = localStorage.getItem('token');
        
        // If no token is found, redirect to login
        if (!token) {
          navigate('/login');
          return;
        }

        // Send the token in the authorization header
        const response = await axios.get('http://localhost:5000/api/employees/me', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // Set the employee data based on the logged-in user
        setEmployee(response.data);
      } catch (error) {
        console.error('Error fetching employee data:', error);
        // Handle unauthorized access, like redirecting to login
        if (error.response.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchEmployeeData();
  }, [navigate]);
  
  return (
    <div className="dashboard-container"> {/* Add a container for styling */}
      <h1>ABC COMPANY Employee Dashboard</h1>
      {employee && (
        <div>
          <h2>Welcome, {employee.name}</h2>
          <p>Email: {employee.email}</p>
        </div>
      )}
      
      <button 
        onClick={() => navigate('/apply-leave')} // Navigate to apply leave page
        className="action-btn" // Add class for styling
      >
        Apply Leave
      </button>
      <button 
        onClick={() => navigate('/leave-history')} // Navigate to leave history page
        className="action-btn" 
      >
        View Leave History
      </button>

      {/* Logout Button */}
      <button onClick={logout} style={{ marginTop: '20px' }}>
        Logout
      </button>
    </div>
  );
}

export default EmployeeDashboard;
