import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 
import './styles/ApplyLeave.css';

function ApplyLeave() {
  const [leaveType, setLeaveType] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [employee, setEmployee] = useState({}); 
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // For navigation

  // Fetch employee details using the JWT token from localStorage
  useEffect(() => {
    const fetchEmployeeDetails = async () => {
      try {
        // Retrieve the token from localStorage
        const token = localStorage.getItem('token');
        
        // If no token is found, redirect to login
        if (!token) {
          navigate('/login');
          return;
        }

        // Send the token in the authorization header to fetch employee data
        const response = await axios.get('http://localhost:5000/api/employees/me', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        // Set the employee data in state
        setEmployee(response.data);
      } catch (error) {
        console.error('Error fetching employee data:', error);
        // Handle unauthorized access, redirect to login
        if (error.response && error.response.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchEmployeeDetails();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const leaveData = {
      employeeId: employee._id, 
      name: employee.name,     
      leaveType,
      startDate,
      endDate,
    };

    try {
      const response = await axios.post('http://localhost:5000/api/leaves/apply', leaveData);
      setMessage('Leave request submitted successfully!');
      console.log(response.data); // Log response for debugging
    } catch (error) {
      setMessage('Error requesting leave. Please try again.');
      console.error('Error requesting leave:', error);
    }
  };

  return (
    <div className="apply-leave-container">
      <h2>Apply for Leave</h2>
      {employee.name && <p>Employee Name: <strong>{employee.name}</strong></p>}
      <form onSubmit={handleSubmit}>
        <select value={leaveType} onChange={(e) => setLeaveType(e.target.value)} required>
          <option value="">Select Leave Type</option>
          <option value="Annual Leave">Annual Leave</option>
          <option value="Sick Leave">Sick Leave</option>
          <option value="Vacation Leave">Vacation Leave</option>
          <option value="Casual Leave">Casual Leave</option>
        </select>
        <p>Start Date</p> 
        <input 
          type="date"
          value={startDate} 
          onChange={(e) => setStartDate(e.target.value)} 
          required 
        />
        <p>End Date</p> 
        <input 
          type="date" 
          value={endDate} 
          onChange={(e) => setEndDate(e.target.value)} 
          required 
        />
        <button className="submit-button" type="submit">Submit Leave Request</button>
      </form>
      {message && <p>{message}</p>}

      {/* Back to Dashboard Button */}
      <button className="back-to-dashboard" onClick={() => navigate('/employee-dashboard')}>
        Back to Dashboard
      </button>
    </div>
  );
}

export default ApplyLeave;
