import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios';
import './styles/AdminDashboard.css';
import { logout } from './logout'; 

function AdminDashboard() {
  const [employees, setEmployees] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const navigate = useNavigate(); 

  // Fetch employees and leave requests when component mounts
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employees');
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    const fetchLeaveRequests = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/leaves');
        setLeaveRequests(response.data);
      } catch (error) {
        console.error('Error fetching leave requests:', error);
      }
    };

    fetchEmployees();
    fetchLeaveRequests();
  }, []);

  // Function to approve or reject leave requests
  const handleLeaveApproval = async (id, status) => {
    try {
      await axios.patch(`http://localhost:5000/api/leaves/${id}`, { status });
      setLeaveRequests((prevRequests) =>
        prevRequests.map((req) => (req.id === id ? { ...req, status } : req))
      );
    } catch (error) {
      console.error('Error updating leave status:', error);
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>ABC COMPANY Admin Dashboard</h1>

      <div className="button-section">
        <button className="toggle-btn" onClick={() => navigate('/manage-employees')}>
          Manage Employees
        </button>
        <button className="toggle-btn" onClick={() => navigate('/manage-leave')}>
          Manage Leave Requests
        </button>
      </div>

      {/* Logout Button */}
      <button onClick={logout} style={{ marginTop: '20px' }}>
        Logout
      </button>

      

      {showLeaveRequests && (
        <div className="leave-requests">
          <h2>Leave Requests</h2>
          <ul>
            {leaveRequests.map((req) => (
              <li key={req._id}>
                <div>
                  <span>{req.employeeName}</span> requested leave from {req.startDate} to {req.endDate}.
                </div>
                <div className="button-group">
                  Status: {req.status}
                  {req.status === 'Pending' && (
                    <>
                      <button onClick={() => handleLeaveApproval(req._id, 'Approved')}>Approve</button>
                      <button onClick={() => handleLeaveApproval(req._id, 'Rejected')}>Reject</button>
                    </>
                  )}
                </div>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
