import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/LeaveHistory.css';

function LeaveHistory() {
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [employeeId, setEmployeeId] = useState('');

  // Fetch employee ID from localStorage 
  React.useEffect(() => {
    const storedId = localStorage.getItem('employeeId'); // Adjust as necessary
    setEmployeeId(storedId);
  }, []);

  useEffect(() => {
    const fetchLeaveRequests = async () => {
      if (employeeId) {
        try {
          const response = await axios.get(`http://localhost:5000/api/leaves/view/${employeeId}`);
          setLeaveRequests(response.data);
        } catch (error) {
          console.error('Error fetching leave history:', error);
        }
      }
    };

    fetchLeaveRequests();
  }, [employeeId]);

  return (
    <div className="leave-history-container">
      <h2>Your Leave History</h2>
      {leaveRequests.length > 0 ? (
        <ul>
          {leaveRequests.map((req) => (
            <li key={req._id}>
              {req.leaveType}: {req.startDate.substring(0, 10)} to {req.endDate.substring(0, 10)} - Status: {req.status}
            </li>
          ))}
        </ul>
      ) : (
        <p>No leave history found.</p>
      )}
    </div>
  );
}

export default LeaveHistory;
