import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeDashboard.css';

function EmployeeDashboard() {
  const [employee, setEmployee] = useState(null);
  const [leaveBalance, setLeaveBalance] = useState(0);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [daysRequested, setDaysRequested] = useState(0);

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Fetch employee data 
        const response = await axios.get('http://localhost:5000/api/employees/me'); 
        setEmployee(response.data);
        setLeaveBalance(response.data.leaveBalance);
        
        // Fetch leave requests for the employee
        const leaveResponse = await axios.get(`http://localhost:5000/api/leaves?employeeId=${response.data._id}`);
        setLeaveRequests(leaveResponse.data);
      } catch (error) {
        console.error('Error fetching employee data:', error);
      }
    };

    fetchEmployeeData();
  }, []);

  const handleLeaveRequest = async (e) => {
    e.preventDefault();
    const leaveData = {
      employeeId: employee._id,
      startDate,
      endDate,
      daysRequested,
    };
    
    try {
      await axios.post('http://localhost:5000/api/leaves', leaveData);
      setLeaveRequests((prevRequests) => [...prevRequests, leaveData]);
      setLeaveBalance((prevBalance) => prevBalance - daysRequested); // Update leave balance based on request
      // Reset the form
      setStartDate('');
      setEndDate('');
      setDaysRequested(0);
    } catch (error) {
      console.error('Error requesting leave:', error);
    }
  };

  const calculateDaysRequested = () => {
    if (startDate && endDate) {
      const start = new Date(startDate);
      const end = new Date(endDate);
      // Calculate the number of days between the two dates
      const timeDiff = end - start;
      const daysDiff = Math.ceil(timeDiff / (1000 * 60 * 60 * 24)) + 1; // +1 to include the start date
      setDaysRequested(daysDiff > 0 ? daysDiff : 0); // Ensure days requested is not negative
    } else {
      setDaysRequested(0);
    }
  };

  return (
    <div>
      <h1>ABC COMPANY Employee Dashboard</h1>
      {employee && (
        <div>
          <h2>Welcome, {employee.name}</h2>
          <p>Email: {employee.email}</p>
          <p>Position: {employee.position}</p>
          <p>Leave Balance: {leaveBalance}</p>
        </div>
      )}

      <h2>Your Leave Requests</h2>
      <ul>
        {leaveRequests.map((req) => (
          <li key={req._id}>
            {req.startDate} to {req.endDate} - Status: {req.status}
          </li>
        ))}
      </ul>

      {/* Leave request form*/}
      <h2>Request Leave</h2>
      <form onSubmit={handleLeaveRequest}>
        <input 
          type="date" 
          name="startDate" 
          value={startDate}
          onChange={(e) => {
            setStartDate(e.target.value);
            calculateDaysRequested(); // Recalculate days when start date changes
          }}
          required 
        />
        <input 
          type="date" 
          name="endDate" 
          value={endDate}
          onChange={(e) => {
            setEndDate(e.target.value);
            calculateDaysRequested(); // Recalculate days when end date changes
          }}
          required 
        />
        <p>Days Requested: {daysRequested}</p> {/* Display calculated days requested */}
        <button type="submit">Submit Leave Request</button>
      </form>

      <h2>Leave History</h2>
      <button onClick={() => { /* Add your navigation logic here */ }}>
        View Leave History
      </button>
    </div>
  );
}

export default EmployeeDashboard;
