import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles/ApplyLeave.css';

function ApplyLeave() {
  const [leaveType, setLeaveType] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [employeeId, setEmployeeId] = useState(''); 
  const [name, setEmployeeName] = useState(''); 
  const [message, setMessage] = useState('');

  // Fetch employee details from localStorage
  useEffect(() => {
    const storedId = localStorage.getItem('employeeId'); 
    const storedName = localStorage.getItem('name'); 
    setEmployeeId(storedId);
    setEmployeeName(storedName); 
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const leaveData = {
      employeeId,
      name, 
      leaveType,
      startDate,
      endDate,
    };

    try {
      const response = await axios.post('http://localhost:5000/api/leaves/apply', leaveData);
      setMessage('Leave request submitted successfully!');
      console.log(response.data); // Log response for debugging
    } catch (error) {
      setMessage('Error requesting leave. Please try again.');
      console.error('Error requesting leave:', error);
    }
  };

  return (
    <div className="apply-leave-container">
      <h2>Apply for Leave</h2>
      <form onSubmit={handleSubmit}>
        <select value={leaveType} onChange={(e) => setLeaveType(e.target.value)} required>
          <option value="">Select Leave Type</option>
          <option value="Sick Leave">Sick Leave</option>
          <option value="Vacation Leave">Vacation Leave</option>
          <option value="Casual Leave">Casual Leave</option>
        </select>
        <p>Start Date</p> 
        <input 
          type="date"
          value={startDate} 
          onChange={(e) => setStartDate(e.target.value)} 
          required 
        />
        <p>End Date</p> 
        <input 
          type="date" 
          value={endDate} 
          onChange={(e) => setEndDate(e.target.value)} 
          required 
        />
        <button type="submit">Submit Leave Request</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default ApplyLeave;
