import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import "./styles"


const ChangePassword = () => {
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');

  const navigate = useNavigate();

  const handleChangePassword = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await axios.put('http://localhost:5000/api/auth/change', { currentPassword, newPassword },
        { headers: { Authorization: `Bearer ${token}` } });
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error changing password');
    }
  };

  return (
    <div>
      <h2>CHANGE PASSWORD</h2>
      <form className="change-form" onSubmit={handleChangePassword}>
        <input
          type="password"
          placeholder="Current Password"
          value={currentPassword}
          onChange={(e) => setCurrentPassword(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="New Password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          required
        />
      </form>
      {message && <p>{message}</p>}
      <button type="submit">Change Password</button>
      {/* Back to Dashboard Button */}
      <button className="back-to-dashboard" onClick={() => navigate('/employee-dashboard')}>
        Back to Dashboard
      </button>
    </div>
  );
};

export default ChangePassword;
