import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 
import './styles/Profile.css';

const Profile = () => {
  const [employee, setEmployee] = useState(null);
  const navigate = useNavigate(); 

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        const token = localStorage.getItem('token');
        
        if (!token) {
          navigate('/login');
          return;
        }

        const response = await axios.get('http://localhost:5000/api/employees/me', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setEmployee(response.data);
      } catch (error) {
        console.error('Error fetching employee data:', error);

        if (error.response?.status === 401) {
          navigate('/login');
        }
      }
    };

    fetchEmployeeData();
  }, [navigate]);

  return (
    <div className="dashboard-container"> 
      <h1>Welcome to Your Profile!</h1>
      {employee ? (
        <div>
          <h2>Name: {employee.name}</h2>
          <h2>Email: {employee.email}</h2>
          <h2>Position: {employee.position}</h2>
          <h2>Salary: {employee.salary}</h2>
          <h2>Leave Balance: {employee.leaveBalance}</h2>
        </div>
      ) : (
        <p>Loading profile information...</p>
      )}
      
      <div className="button-group">
        <button onClick={() => navigate('/change')} className="button">
          Change Password
        </button>

        <button className="back-to-dashboard" onClick={() => navigate('/employee-dashboard')}>
          Back to Dashboard
        </button>
      </div>
    </div>
  );
}

export default Profile;
