import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EmployeeDashboard.css';

function EmployeeDashboard() {
  const [employee, setEmployee] = useState(null);
  const [leaveBalance, setLeaveBalance] = useState(0);
  const [leaveRequests, setLeaveRequests] = useState([]);

  useEffect(() => {
    const fetchEmployeeData = async () => {
      try {
        // Fetch employee data 
        const response = await axios.get('http://localhost:5000/api/employees/me'); 
        setEmployee(response.data);
        setLeaveBalance(response.data.leaveBalance);
        
        // Fetch leave requests for the employee
        const leaveResponse = await axios.get(`http://localhost:5000/api/leaves?employeeId=${response.data._id}`);
        setLeaveRequests(leaveResponse.data);
      } catch (error) {
        console.error('Error fetching employee data:', error);
      }
    };

    fetchEmployeeData();
  }, []);

  const handleLeaveRequest = async (leaveData) => {
    try {
      await axios.post('http://localhost:5000/api/leaves', leaveData);
      setLeaveRequests((prevRequests) => [...prevRequests, leaveData]);
      setLeaveBalance((prevBalance) => prevBalance - leaveData.daysRequested); // Update leave balance based on request
    } catch (error) {
      console.error('Error requesting leave:', error);
    }
  };

  return (
    <div>
      <h1>ABC COMPANY Employee Dashboard</h1>
      {employee && (
        <div>
          <h2>Welcome, {employee.name}</h2>
          <p>Email: {employee.email}</p> {/* Display the email */}
          <p>Position: {employee.position}</p>
          <p>Leave Balance: {leaveBalance}</p>
        </div>
      )}

      <h2>Your Leave Requests</h2>
      <ul>
        {leaveRequests.map((req) => (
          <li key={req._id}>
            {req.startDate} to {req.endDate} - Status: {req.status}
          </li>
        ))}
      </ul>

      {/* Leave request form (simple) */}
      <h2>Request Leave</h2>
      <form onSubmit={(e) => {
        e.preventDefault();
        const leaveData = {
          employeeId: employee._id,
          startDate: e.target.startDate.value,
          endDate: e.target.endDate.value,
          daysRequested: e.target.daysRequested.value,
        };
        handleLeaveRequest(leaveData);
      }}>
        <input type="date" name="startDate" required />
        <input type="date" name="endDate" required />
        <input type="number" name="daysRequested" required placeholder="Days Requested" />
        <button type="submit">Submit Leave Request</button>
      </form>
    </div>
  );
}

export default EmployeeDashboard;
