import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

function AdminDashboard() {
  const [employees, setEmployees] = useState([]);
  const [leaveRequests, setLeaveRequests] = useState([]);

  // Fetch employees and leave requests when component mounts
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/employees');
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    const fetchLeaveRequests = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/leaves');
        setLeaveRequests(response.data);
      } catch (error) {
        console.error('Error fetching leave requests:', error);
      }
    };

    fetchEmployees();
    fetchLeaveRequests();
  }, []);

  // Function to approve or reject leave requests
  const handleLeaveApproval = async (id, status) => {
    try {
      await axios.patch(`http://localhost:5000/api/leaves/${id}`, { status });
      setLeaveRequests((prevRequests) =>
        prevRequests.map((req) => (req.id === id ? { ...req, status } : req))
      );
    } catch (error) {
      console.error('Error updating leave status:', error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <h2>Manage Employees</h2>
      <ul>
        {employees.map((emp) => (
          <li key={emp._id}>
            {emp.name} - {emp.position}
          </li>
        ))}
      </ul>

      <h2>Manage Leave Requests</h2>
      <ul>
        {leaveRequests.map((req) => (
          <li key={req._id}>
            {req.employeeName} requested leave from {req.startDate} to {req.endDate}. 
            Status: {req.status}
            {req.status === 'Pending' && (
              <>
                <button onClick={() => handleLeaveApproval(req._id, 'Approved')}>Approve</button>
                <button onClick={() => handleLeaveApproval(req._id, 'Rejected')}>Reject</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminDashboard;
